INSERT INTO beer (name, brewery_id, style, abv, ibu, description, imageURL) VALUES
('60$ Nachos', 1, 'Double IPA', 8.0, 80, 'Intensely hoppy ale with a bold bitterness and tropical fruit flavors.', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712679976/hoof_dipa_btjcru.jpg'),
('$120 Nachos', 1, 'Triple IPA', 12, 80, 'Triple Dry-Hopped Triple IPA with Citra Hops', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQrMwJkNDn0fuzazejM-CsTPqY0DRsjIScDTvFiVeUtCQ&s'),
('Fitness Freak', 1, 'Berliner Weisse', 4.0, 5, 'Tart and refreshing wheat beer with a clean, crisp finish.', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712679962/hoof_fake_beer_flsfg8.jpg'),
('Belloq', 1, 'Stout with Coffee and Vanilla', 4.2, 10, 'Smooth caramel & chocolate notes get a massive coffee rogering. Competitively intellectual flavor that makes you feel smarter than you look. Pair with a corduroy blazer and look out world.', 'https://popmenucloud.com/cdn-cgi/image/width%3D1200%2Cheight%3D1200%2Cfit%3Dscale-down%2Cformat%3Dauto%2Cquality%3D60/frhniukm/f16976bc-48f3-4c5d-9cd6-c34f759b5c9b.jpg'),
('Club Paradise', 1, 'Sour Ale', 8, 20, 'Our Rotating Tiki Cocktail Inspired Sour IPA brewed with Fruit Purée, Vanilla & Milk Sugar', 'https://media.istockphoto.com/id/636828412/photo/light-cold-beer-in-frosty-glass-isolated-on-white.jpg?s=612x612&w=0&k=20&c=u7vi52UAj21nx4Az83-Hn4MjuHTE1LZnrqnpAjSr8j0='),
('Are we Having Fun Yet?', 1, 'American Pale Ale', 6, 35, 'Double dry-hopped with Belma, Citra & Mosaic, this pillowy soft ale delivers massive notes of strawberry, melon & papaya.', 'https://media.istockphoto.com/id/585479204/photo/glass-of-beer-on-white-background.webp?b=1&s=170667a&w=0&k=20&c=OFGBmP6OAfpeGFTf0fKgKQFOsFHVFe-wH8Y8gEgwOe0='),

('Dire Wolf', 2, 'Porter', 10.5, 35, 'Smooth and velvety ale with notes of roasted malt, dark chocolate, and a hint of smoke.', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712679524/wolf_fake_porter_fxuodv.jpg'),
('Daybreak', 2, 'Coffee Vanilla Cream Ale', 5, 14, 'Light-bodied brilliant clarity iced coffee beer.', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712679920/wolf_fake_belgian_pmavus.jpg'),
('Wolfs Ridge Brewery IPA', 2, 'American IPA', 6.8, 70, 'This year-round unfiltered American IPA is all about the hops in every way. Layered hopping techniques provide a perfect balance of hop flavor, aroma, and bitterness, ranging from tropical citrus fruit to hints of sticky pine.', 'https://store.wolfsridgebrewing.com/cdn/shop/products/WRB_WRBIPA_1024x1024@2x.png?v=1675352617'),
('Hazy IPA', 2, 'Hazy IPA', 6.5, 25, 'Our new core Hazy IPA is all about the hops, featuring Simcoe and Mosaic for classic citrus and pine flavors, as well as late-addition Cryo Amarillo for an extra aromatic punch of tropical fruit and berry. A soft, pillowy mouthfeel keeps our Hazy from imparting too much lingering bitterness, a perfectly balanced brew to enjoy for any occasion, any time of year!', 'https://store.wolfsridgebrewing.com/cdn/shop/files/WRB_WRBHazy_nb_1024x1024@2x.png?v=1706289036'),
('Walking Taco', 2, 'Mexican-style Lager with Lime', 4.5, 9,'A classic Mexican brew gets the Midwestern treatment! Like tasty tacos crunched up in your favorite bag of chips, our Mexican-style lager with lime is sure to be a crushable crowd pleaser for any occasion. Whether you’re on the go or posted up on a patio, there’s nothing like a Walking Taco!', 'https://store.wolfsridgebrewing.com/cdn/shop/products/WRB_WalkingTaco_final_1024x1024@2x.png?v=1677777782'),
('Dream Boat', 2, 'Oatmeal Stout', 5.2, 21, 'How do you make a "dreamy" stout?  Just add oats! We took a classic stout recipe and brewed it with 800 lbs of flaked oats, providing an ultra creamy body and smooth mouthfeel. Rich flavors of bakers chocolate and coffee will gently float along your palate; one sip is all it takes to fall in love!', 'https://store.wolfsridgebrewing.com/cdn/shop/files/WRB_DreamBoat_1024x1024@2x.png?v=1703182065'),


('Knotty Pine Amber Ale', 3, 'Amber Ale', 5.5, 20, 'A moderate, hoppy beer with a distinct caramel malty flavor.', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712679945/knotty-pine-amber-ale_jxcjfb.jpg'),
('Knotty Pine Jays', 3, 'German Pils', 4.5, 10, 'Light, crisp, maltiness and a clean heady finish are just a taste of what to expect from "Jays" German Lager', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712679932/knotty-pine-pine-lite_mhncxx.jpg'),
('Knotty Pine Drip So Hard', 3, 'American Porter', 5.8, 35, 'The "Drip so Hard" Coffee Porter is a smooth drinking porter with an abundance of coffee notes throughout and a delicious chocolate finish.', 'https://popmenucloud.com/cdn-cgi/image/width%3D1200%2Cheight%3D1200%2Cfit%3Dscale-down%2Cformat%3Dauto%2Cquality%3D60/frhniukm/f16976bc-48f3-4c5d-9cd6-c34f759b5c9b.jpg'),
('Knotty Pine Seren-DIPA-ty', 3, 'Double IPA', 8.2, 20, 'An intensely hoppy pale ale with minimal malt character, designed to showcase its strong hop flavors.', 'https://media.istockphoto.com/id/636828412/photo/light-cold-beer-in-frosty-glass-isolated-on-white.jpg?s=612x612&w=0&k=20&c=u7vi52UAj21nx4Az83-Hn4MjuHTE1LZnrqnpAjSr8j0='),
('Knotty Pine Lite', 3, 'American Lite Lager', 5, 10, 'A low calorie and a low alcohol craft brew thats light tasting and perfect game day drinking.', 'https://media.istockphoto.com/id/1094539664/photo/glass-of-lager-beer.jpg?s=612x612&w=0&k=20&c=uYixkaHfCn_UIwaoINfeientg81e0rhNv6nyou0QBf4='),
('Knotty Pine One More', 3, 'American Pale Ale', 4.5, 10, 'A moderate, refreshing ale with noticeable hop aroma balanced by supporting malt flavors.', 'https://assets.bonappetit.com/photos/57e07c397c2b8d77607818b3/master/w_1600%2Cc_limit/saison-beer-646.jpg'),

('Wild Card Saison', 4, 'Saison', 6.1, 25, 'Wild Card is a hazy pale orange French farmhouse style ale brewed with a kiss of cardamom. Calais yeast produces fruity, peppery esters that play well with the piney, fruity spice,', 'https://614now.com/wp-content/uploads/2018/03/12002936_10153120887531592_4503233706222057652_n.jpg'),
('Imperial Stout', 4, 'Stout', 7.6, 35, 'Dark and roasty ale with flavors of coffee, chocolate, and a hint of smoke.', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712680005/barley_dark_beer_mpxpfa.jpg'),
('Bourbon Meyer Buckeye Stout (Tulip)',  4, 'Stout', 9.6, 40, 'BMBS starts with our Russian Imperial Stout at its base. We age it in wet bourbon barrels and add organic cacao nib and peanut butter.', 'https://cheersall.com/cdn/shop/articles/cheers_all_stout_5.19_1024x1024.jpg?v=1621451552'),
('Rye Eye Eye IIPA', 4, 'Double IPA', 8.2, 15, 'This golden Double IPAs mash bill includes a copious amount of American rye, which contributes notes of spice and dill. The bulk of the mash is Pale Ale malt from Montana with a smidgen of toasted malt to emphasize the bready malt flavor.', 'https://media.istockphoto.com/id/636828412/photo/light-cold-beer-in-frosty-glass-isolated-on-white.jpg?s=612x612&w=0&k=20&c=u7vi52UAj21nx4Az83-Hn4MjuHTE1LZnrqnpAjSr8j0='),
('Hazy Odyssey', 4, 'Juicy/ Hazy IPA', 6, 15, 'Barleys take on the juicy/hazy IPA that has a pleasant hop profile evoking flavors of mango and pineapple with a slight bitterness that is mellowed by the addition of lactose.', 'https://t3.ftcdn.net/jpg/03/02/35/92/360_F_302359234_dizVa9tYyP9Zh2UPxzCVqDrc81meOv1s.jpg'),
('Frost Hop Cold IPA', 4, 'Crisp IPA', 7.4, 10, 'This super crisp IPA is piney, spicy, and bursting with citrus. Liam, Barleys Assistant Brewer, suggested we brew our first Cold IPA and include resinous Chinook hops paired with Citra and Mosaic.', 'https://media.istockphoto.com/id/585479204/photo/glass-of-beer-on-white-background.webp?b=1&s=170667a&w=0&k=20&c=OFGBmP6OAfpeGFTf0fKgKQFOsFHVFe-wH8Y8gEgwOe0='),


('Bodhi', 5, 'Double IPA', 8.5, 80, 'Bodhi is a classic double IPA from Columbus Brewing Company, known for its bold hop flavors and citrusy aroma.', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712680511/bodhi-440x716_mtz4nj.webp'),
('Creeper', 5, 'Imperial Stout', 10.2, 65, 'Creeper is a rich and robust imperial stout brewed by Columbus Brewing Company, featuring notes of chocolate, coffee, and caramel.', 'https://res.cloudinary.com/dknqgztin/image/upload/v1712680567/creeper_dj9ctj.webp'),
('Devils Kush', 5, 'IPA', 6.66, 15, 'The king of the dank side—Devils Kush brings the hops through a blend of Pacific Northwest hops including Simcoe, Idaho 7, Strata and Chinook. It is intensely hoppy with subtle notes of citrus and tropical fruit backed up by a sinister 6.66% ABV.', 'https://columbusbrewing.com/wp-content/uploads/2023/01/Website-Devils-Kush-1.png'),
('Columbus Lager', 5, 'Lager', 4.6, 18, 'Columbus Lager is an instant classic. The 2022 World Beer Cup Bronze winner pours with a timeless, bright golden hue with a lofty white head. A clean and crisp beer with each element working in perfect harmony, which retains the history and tradition of the style.', 'https://columbusbrewing.com/wp-content/uploads/2022/03/lager.png'),
('Nug Wizard', 5, 'Experimental IPA', 7.6, 22, '5 nugs to rule them all! Nug Wizard is a magical brew featuring a 5-hop alchemy of experimental varieties that rain down notes of citrus and stone fruit.', 'https://columbusbrewing.com/wp-content/uploads/2024/01/Nug-Wizard.png'),
('Margarita Playa', 5, 'Tart Ale with Lime and Salt', 4.6, 7, 'A refreshing, tart ale to put you in vacation mode. Every sip of Margarita Playa tastes like the perfect beach day with aromas of freshly-squeezed margarita and salty ocean air. Brewed with lime concentrate and salt, it resembles the bright sunshine and refreshes your taste buds.', 'https://columbusbrewing.com/wp-content/uploads/2022/12/Margarita-Playa-web-render.png'),

('Susie', 6, 'New England IPA', 7, 37, 'Juicy hazy New England IPA with prominent notes of grapefruit', 'https://images.squarespace-cdn.com/content/v1/6192e85d8c6bc27fc2530eec/8991e55f-fff8-4676-9522-7ddf9bbe4b14/SUSIE-aluminium-can-mockup.png?format=1000w'),
('Belgian Aloha', 6, 'Belgian Tripel', 9.6, 28, 'Brewed with Hibiscus with a floral flavor reminiscent of rose wine. 2019 Ohio Craft Brewers Cup Medal Winner', 'https://images.squarespace-cdn.com/content/v1/6192e85d8c6bc27fc2530eec/4d2befad-b9eb-4902-b0e5-cef374b9f8c6/ALOHA-aluminium-can-mockup.png?format=1000w'),
('Alum Creek', 6, 'Beach Blonde', 5.4, 27, 'Refreshing and crisp Blonde Ale. Perfect for a hot day or casual drinking.', 'https://images.squarespace-cdn.com/content/v1/6192e85d8c6bc27fc2530eec/1595c687-5d01-4649-8479-e8b3b7b589cb/ALUM+CREEK-aluminium-can-mockup.png?format=1000w'),
('Dustys Done Deal', 6, 'Double New England IPA', 9.2, 47, 'Delicious hazy IPA that is light in flavor and body. Careful this ones sneaky.', 'https://images.squarespace-cdn.com/content/v1/6192e85d8c6bc27fc2530eec/96c9659e-cbf3-4948-8a13-d82197792803/DUSTY%27S-aluminium-can-mockup.png?format=1000w'),
('Roosevelt Coffee Stout', 6, 'Coffee Stout', 5.5, 20, 'This beer is light in body and brewed with Olentangy River Brewing Companys cold brew concentrate. *Does contain a bit of caffiene.', 'https://images.squarespace-cdn.com/content/v1/6192e85d8c6bc27fc2530eec/5394f57e-7d48-484e-9189-cdaee6f893c2/ROOSEVELT-aluminium-can-mockup.png?format=1000w'),
('Ol n Tangy Classico', 6, 'Gose', 4.8, 25, 'A crisp tart sour brewed with salt made to be very refreshing. One that you can always come back to.', 'https://images.squarespace-cdn.com/content/v1/6192e85d8c6bc27fc2530eec/37a44a48-8ee0-4a2a-8ae3-8885c8eabb94/OL%27N+TANGY-aluminium-can-mockup.png?format=1000w'),

